/* @noodom / Scénario de gestion des plugins Jeedom depuis Discord (Sauvegarde, Restauration, Mise à jour, Suppression de sauvegardes)
Nom du scénario : Demande d actions sur plugins

Installation du menu : https://github.com/noodom/jeedom_scenarios/blob/master/updatePlugins/README.md
- Menu des actions possibles sur les plugins
- Exécute les actions demandées par l'utilisateur avec l'appel du scénario "Exécution d actions sur plugins"

/!\ ATTENTION /!\ Ce scénario modifie le contenu du répertoires contenant les plugins Jeedom. 
/!\ Je vous laisse seul juge de la pertinence et de votre confiance à utiliser ce scénario pour la gestion de l'installation de vos plugins.
/!\ Je décline donc toute responsabilité pour tout problème créé suite à l'exécution de ce scénario./!\ 

/!\ Définir l'identifiant du scénario "Exécution d actions sur plugins Discord" avec la valeur du tag scenarioExecutionActionsPluginsId en début de scénario
/!\ Définir l'identifiant du scénario "update plugins Discord" de l'équipement Discord utilisé avec la valeur du tag scenarioUpdatePluginsId en début de scénario
/!\ Définir l'identifiant de la commande "Envoi message évolué" de l'équipement Discord utilisé avec la valeur du tag discordMessageEvolueId en début de scénario
/!\ Définir le nombre de plugins contenus dans chaque groupe de plugins affichés avec la valeur du tag nbPluginsGrouped en début de scénario
/!\ Définir le répertoire de sauvegarde des plugins utilisé par ce scénario avec la valeur du tag backupPath en début de scénario (modification facultative)

*/

$scenario->setLog('Lancement des actions sur les plugins');

$tags = $scenario->getTags();

// Valeurs par défaut (si non définis en début de scénario)
(!isset($tags['#discordMessageEvolueId#'])) ? $tags['#discordMessageEvolueId#'] = -1 : null;
(!isset($tags['#nbPluginsGrouped#'])) ? $tags['#nbPluginsGrouped#'] = 20 : null;
(!isset($tags['#pluginsPath#'])) ? $tags['#pluginsPath#'] = "/var/www/html/plugins" : null;
(!isset($tags['#backupPath#'])) ? $tags['#backupPath#'] = "/var/www/html/data/custom/plugins_backup" : null;

// ID de la commande "Envoi message évolué" d'un équipement du plugin Discord
define('DISCORD_MESSAGE_EVOLUE_ID', $tags['#discordMessageEvolueId#']);

// Signature du scénario pour messages Discord
$tags['#scenarioName#'] = 'Exécuté par : ' . $scenario->getName();

/* Fonctions de manipulations des tableaux de plugins */

function pluginsFilter ($dirName) {
  return $dirName != '.' && $dirName != '..';
}

function pluginRenderer($v, $k)
{
  $numberConversion = [':zero:', ':one:', ':two:', ':three:', ':four:', ':five:', ':six:', ':seven:', ':eight:', ':nine:'];
  // création du code Emoji
  $val = $k+1;
  $pluginNumber = '';
  while($val!=0) {
    $mod = $val%10;
    $pluginNumber = $numberConversion[$mod] . $pluginNumber;
    $val-=$mod;
    $val/=10;
  }

  // création du libellé du plugin pour la liste des plugins proposés
  return $pluginNumber.' : '.$v;
}

function pluginsConcat($p1,$p2) {
  return $p1.','.$p2;
}

function createMessage($message, $plugin) {
  $message .= $plugin . ' , ';
  return $message;
}

function sendMessage($msgList) {
  $msgToSend = trim(array_reduce($msgList, "createMessage"), ', ');
  try {
    $cmd = cmd::byId(DISCORD_MESSAGE_EVOLUE_ID);
    $options = array('titre'=>'', 'description'=> $msgToSend, 'colors'=> '#0000FF', 'footer'=> '');
    $cmd->execCmd($options);
  }
  catch(Exception $e){
    log::add('scenario', 'error', 'Erreur d\'envoi de message Discord [Liste de plugins]');
  }
}

// définitions asks, titres, descriptions
$tags['#askActions#'] = "Quelle action souhaitez-vous effectuer sur les plugins ?";
$tags['#askSauvegarder#'] = "Quel plugin faut-il sauvegarder ?"."\n"."(Saisir le numéro du plugin souhaité ou '0' pour 'annuler')";
$tags['#askRestaurer#'] = "Quel plugin souhaitez-vous restaurer ?"."\n"."(Saisir le numéro du plugin souhaité ou '0' pour 'annuler')";
$tags['#askSupprimer#'] = "Quel plugin souhaitez-vous supprimer ?"."\n"."(Saisir le numéro du plugin souhaité ou '0' pour 'annuler')";

$tags['#titreSauvegarder#'] = "Sauvegarde de plugin";
$tags['#titreRestaurer#'] = "Restauration de plugin";
$tags['#titreSupprimerSauvegarde#'] = "Suppression de sauvegarde de plugin";
$tags['#titreAnnulerActions#'] = "Annulation des actions sur plugins";
$tags['#titreActionInconnue#'] = "Action inconnue sur les plugins";
$tags['#titreErreurInstallation#'] = "Erreur à l'installation";

$tags['#descriptionSauvegardeAnnulee#'] = ":red_circle: Sauvegarde de plugin annulée";
$tags['#descriptionSauvegardeErreur#'] = ":red_circle: Erreur de saisie du numéro de plugin"."\n"."(numéro invalide ou ne pas saisir le nom du plugin)";
$tags['#descriptionRestaurationAucunPlugin#'] = ":red_circle: Aucun plugin n'est actuellement sauvegardé : restauration impossible";
$tags['#descriptionRestaurationAnnulee#'] = ":red_circle: Restauration de plugin annulée";
$tags['#descriptionRestaurationErreur#'] = ":red_circle: Erreur de saisie du numéro de plugin"."\n"."(numéro invalide ou ne pas saisir le nom du plugin)";
$tags['#descriptionSupprimerSauvegardeAucunPlugin#'] = ":red_circle: Aucun plugin n'est actuellement sauvegardé :"."\n"."suppression de sauvegarde de plugins impossible";
$tags['#descriptionErreurInstallation#'] = ':red_circle: Vous devez renseigner ces tags en début de scénario :'. "\n\n"
	.(($tags['#scenarioExecutionActionsPluginsId#'] == -1)?' - scenarioExecutionActionsPluginsId'."\n":'')
    .(($tags['#scenarioUpdatePluginsId#'] == -1)?' - scenarioUpdatePluginsId'."\n":'')
    .(($tags['#discordMessageEvolueId#'] == -1)?' - discordMessageEvolueId':'');

$tags['#descriptionSupprimerSauvegardeAnnulee#'] = ":red_circle: Suppression de sauvegarde de plugin annulée";
$tags['#descriptionSupprimerSauvegardeErreur#'] = ":red_circle: Erreur de saisie du numéro de plugin"."\n"."(numéro invalide ou ne pas saisir le nom du plugin)";
$tags['#descriptionAnnulerActions#'] = ":red_circle: Actions sur les plugins annulées";
$tags['#descriptionActionInconnue)#'] = ":red_circle: L'action \"variable(askActionOnPlugin)\" demandée sur les plugins n'est pas gérée";

$scenario->setTags($tags);

$tags = $scenario->getTags();

// Demande de backup d'un plugin
// Récupération de la liste des plugins installés
$dirs = array_values(array_filter(scandir($tags['#pluginsPath#']), "pluginsFilter"));
$scenario->setLog('Nombre de plugins installés : ' . sizeof($dirs));

$tags['#installedPluginsList#'] = '';
$tags['#installedPluginsSize#'] = sizeof($dirs);
if ($tags['#installedPluginsSize#'] != 0) {
  $pluginList = array_map('pluginRenderer', $dirs, array_keys($dirs));
  // Découpage des plugins par paquets
  $chunkPluginsList = array_chunk($pluginList, $tags['#nbPluginsGrouped#']);
  $scenario->setLog('Nombre de messages : ' . sizeof($chunkPluginsList));
  // Envoi de la liste des plugins par paquets
  array_map('sendMessage', $chunkPluginsList);
}

$scenario->setTags($tags);

$tags = $scenario->getTags();

// Demande de sauvegarde d'un plugin
// Récupération de la liste des plugins installés
$scenario->setLog('Récupération de la liste des plugins installés dans le répertoire ' . $tags['#pluginsPath']);
$dirs = array_values(array_filter(scandir($tags['#pluginsPath#']), "pluginsFilter"));
$pluginId = $scenario->getData("askPluginChoice");
$tags['#pluginToBackup#'] = (is_numeric($pluginId) && $pluginId <= sizeof($dirs)) ? $dirs[$pluginId - 1] : "";

$scenario->setLog("Lancement du backup du plugin " . $tags['#pluginToBackup#']);

$scenario->setTags($tags);

$tags = $scenario->getTags();

// Demande de restauration d'un plugin
// Récupération de la liste des plugins sauvegardés
$dirs = array_values(array_filter(scandir($tags['#backupPath#']), "pluginsFilter"));
$scenario->setLog('Nombre de plugins sauvegardés : ' . sizeof($dirs));

$tags['#savedPluginsList#'] = '';
$tags['#savedPluginsSize#'] = sizeof($dirs);
if ($tags['#savedPluginsSize#'] != 0) {
  $pluginList = array_map('pluginRenderer', $dirs, array_keys($dirs));
  // Découpage des plugins par paquets
  $chunkPluginsList = array_chunk($pluginList, $tags['#nbPluginsGrouped#']);
  $scenario->setLog('Nombre de messages : ' . sizeof($chunkPluginsList));
  // Envoi de la liste des plugins par paquets
  array_map('sendMessage', $chunkPluginsList);
}

$scenario->setTags($tags);

$tags = $scenario->getTags();

// Demande de récupération d'un plugin
// Récupération de la liste des plugins sauvegardés
$scenario->setLog('Récupération de la liste des plugins sauvegardés dans le répertoire ' . $tags['#backupPath#']);
$dirs = array_values(array_filter(scandir($tags['#backupPath#']), "pluginsFilter"));
$pluginId = $scenario->getData("askPluginToRestore");
$tags['#pluginToRestore#'] = (is_numeric($pluginId) && $pluginId <= sizeof($dirs)) ? $dirs[$pluginId - 1] : "";

$scenario->setLog("Lancement de la restauration du plugin " . $tags['#pluginToRestore#']);

$scenario->setTags($tags);

$tags = $scenario->getTags();

// Récupération de la liste des plugins sauvegardés
$scenario->setLog('Récupération de la liste des plugins sauvegardés dans le répertoire ' . $tags['#backupPath#']);
$dirs = array_values(array_filter(scandir($tags['#backupPath#']), "pluginsFilter"));
$scenario->setLog('liste répertoires sauvegardés : ' . $dirs);

$tags['#pluginsToRemoveList#'] = '';
$tags['#pluginsToRemoveSize#'] = sizeof($dirs);
if ($tags['#pluginsToRemoveSize#'] != 0) {
  $pluginList = array_map('pluginRenderer', $dirs, array_keys($dirs));
  // Découpage des plugins par paquets
  $chunkPluginsList = array_chunk($pluginList, $tags['#nbPluginsGrouped#']);
  $scenario->setLog('Nombre de messages : ' . sizeof($chunkPluginsList));
  // Envoi de la liste des plugins par paquets
  array_map('sendMessage', $chunkPluginsList);
}

$scenario->setTags($tags);

$tags = $scenario->getTags();

// Demande de suppression d'une sauvegarde de plugin
// Récupération de la liste des plugins sauvegardés
$scenario->setLog('Récupération de la liste des plugins sauvegardés dans le répertoire ' . $tags['#backupPath#']);
$dirs = array_values(array_filter(scandir($tags['#backupPath#']), "pluginsFilter"));
$pluginId = $scenario->getData("askPluginToRemove");
$tags['#pluginToRemove#'] = (is_numeric($pluginId) && $pluginId <= sizeof($dirs)) ? $dirs[$pluginId - 1] : "";

$scenario->setLog("Lancement de la suppression de la sauvegarde du plugin " . $tags['#pluginToRemove#']);

$scenario->setTags($tags);
