/* @noodom / Scénario de gestion des plugins Jeedom depuis Telegram (Sauvegarde, Restauration, Mise à jour, Suppression de sauvegardes)
Nom du scénario : Demande d actions sur plugins Telegram

Installation du scénario : https://github.com/noodom/jeedom_scenarios/blob/master/updatePlugins/README.md

Description :
- Affichage d'un menu des actions possibles sur les plugins Jeedom
- Exécute les actions demandées par l'utilisateur avec l'appel du scénario "Exécution d actions sur plugins"

/!\ ATTENTION /!\ Ce scénario modifie le contenu du répertoires contenant les plugins Jeedom. 
/!\ Je vous laisse seul juge de la pertinence et de votre confiance à utiliser ce scénario pour la gestion de l'installation de vos plugins.
/!\ Je décline donc toute responsabilité pour tout problème créé suite à l'exécution de ce scénario./!\ 

/!\ Réactiver les 4 commandes "scenario" et sélectionner les scénarios "Exécution d actions sur plugins" et "update plugins" (voir les commentaires avant ces commandes)
/!\ Définir l'identifiant de la commande "Envoi message utilisateur" de l'équipement Telegram utilisé avec la valeur du tag telegramMessageId en début de scénario
/!\ Définir le nombre de plugins contenus dans chaque groupe de plugins affichés avec la valeur du tag nbPluginsGrouped en début de scénario
/!\ Définir le répertoire contenant la liste des plugins installés avec la valeur du tag pluginsPath en début de scénario (pas de raison de le modifier à priori)
/!\ Définir le répertoire de sauvegarde des plugins utilisé par ce scénario avec la valeur du tag backupPath en début de scénario

*/

$scenario->setLog('Lancement des actions sur les plugins');

$tags = $scenario->getTags();

$tags['#redCircle#'] = "&"."#x1F534;";
$tags['#greenCircle#'] = "&"."#x1F7E2;";
$tags['#greenCheck#'] = "&"."#x2705;";
$tags['#whiteCheck#'] = "&"."#x2611;";
$tags['#arrow#'] = "&"."#x27A1;";

// Valeurs par défaut (si non définis en début de scénario)
(!isset($tags['#telegramMessageId#'])) ? $tags['#telegramMessageId#'] = -1 : null;
(!isset($tags['#nbPluginsGrouped#'])) ? $tags['#nbPluginsGrouped#'] = 20 : null;
(!isset($tags['#pluginsPath#'])) ? $tags['#pluginsPath#'] = "/var/www/html/plugins" : null;
(!isset($tags['#backupPath#'])) ? $tags['#backupPath#'] = "/var/www/html/data/custom/plugins_backup" : null;

// ID de la commande "Envoi message évolué" d'un équipement du plugin Discord
define('TELEGRAM_MESSAGE_ID', $tags['#telegramMessageId#']);

// Signature du scénario pour messages Discord
$tags['#scenarioName#'] = 'Exécuté par : ' . $scenario->getName();

/* Fonctions de manipulations des tableaux de plugins */

function pluginsFilter ($dirName) {
  return $dirName != '.' && $dirName != '..';
}

function pluginRenderer($v, $k)
{
  // TODO : remplacer par des emojis pour Telegram
  //$numberConversion = [':zero:', ':one:', ':two:', ':three:', ':four:', ':five:', ':six:', ':seven:', ':eight:', ':nine:'];
  $numberConversion = ["&"."#x0030;", "&"."#x0031;", "&"."#x0032;", "&"."#x0033;", "&"."#x0034;", "&"."#x0035;", "&"."#x0036;", "&"."#x0037;", "&"."#x0038;", "&"."#x0039;"];
  // création du code Emoji
  $val = $k+1;
  $pluginNumber = '';
  while($val!=0) {
    $mod = $val%10;
    $pluginNumber = $numberConversion[$mod] . $pluginNumber;
    $val-=$mod;
    $val/=10;
  }

  // création du libellé du plugin pour la liste des plugins proposés
  return '<b>'.$pluginNumber.'</b> : '.$v;
}

function pluginsConcat($p1,$p2) {
  return $p1.','.$p2;
}

function createMessage($message, $plugin) {
  $message .= $plugin . ' , ';
  return $message;
}

function sendMessage($msgList) {
  $msgToSend = trim(array_reduce($msgList, "createMessage"), ', ');
  try {
    $cmd = cmd::byId(TELEGRAM_MESSAGE_ID);
    $cmd->execCmd($options=array('title'=> "empty=1",'message'=> $msgToSend), $cache=0);
  }
  catch(Exception $e){
    log::add('scenario', 'error', 'Erreur d\'envoi de message Telegram [Liste de plugins]');
  }
}

// définitions asks, titres, descriptions
$tags['#askActions#'] = $tags['#arrow#']." Quelle action souhaitez-vous effectuer sur les plugins ?";
$tags['#askSauvegarder#'] = $tags['#arrow#']." Quel plugin faut-il sauvegarder ?"."\n\n"."(Saisir le <b>numéro du plugin</b> souhaité ou <b>0</b> pour <b>annuler</b>)";
$tags['#askRestaurer#'] = $tags['#arrow#']." Quel plugin souhaitez-vous restaurer ?"."\n\n"."(Saisir le <b>numéro du plugin</b> souhaité ou <b>0</b> pour <b>annuler</b>)";
$tags['#askSupprimer#'] = $tags['#arrow#']." Quel plugin souhaitez-vous supprimer ?"."\n\n"."(Saisir le <b>numéro du plugin</b> souhaité ou <b>0</b> pour <b>annuler</b>)";

$tags['#titreSauvegarder#'] = $tags['#whiteCheck#']." Sauvegarde de plugin ".$tags['#whiteCheck#'];
$tags['#titreRestaurer#'] = $tags['#whiteCheck#']." Restauration de plugin ".$tags['#whiteCheck#'];
$tags['#titreSupprimerSauvegarde#'] = $tags['#whiteCheck#']." Suppression de sauvegarde de plugin ".$tags['#whiteCheck#'];
$tags['#titreAnnulerActions#'] = $tags['#whiteCheck#']." Annulation des actions sur plugins ".$tags['#whiteCheck#'];
$tags['#titreActionInconnue#'] = $tags['#whiteCheck#']." Action inconnue sur les plugins ".$tags['#whiteCheck#'];

$tags['#descriptionSauvegardeAnnulee#'] = $tags['#redCircle#']." Sauvegarde de plugin annulée";
$tags['#descriptionSauvegardeErreur#'] = $tags['#redCircle#']." Erreur de saisie du numéro de plugin"."\n"."(numéro invalide ou ne pas saisir le nom du plugin)";
$tags['#descriptionRestaurationAucunPlugin#'] = $tags['#redCircle#']." Aucun plugin n'est actuellement sauvegardé : restauration impossible";
$tags['#descriptionRestaurationAnnulee#'] = $tags['#redCircle#']." Restauration de plugin annulée";
$tags['#descriptionRestaurationErreur#'] = $tags['#redCircle#']." Erreur de saisie du numéro de plugin"."\n"."(numéro invalide ou ne pas saisir le nom du plugin)";
$tags['#descriptionSupprimerSauvegardeAucunPlugin#'] = $tags['#redCircle#']." Aucun plugin n'est actuellement sauvegardé :"."\n"."suppression de sauvegarde de plugins impossible";

$tags['#descriptionSupprimerSauvegardeAnnulee#'] = $tags['#redCircle#']." Suppression de sauvegarde de plugin annulée";
$tags['#descriptionSupprimerSauvegardeErreur#'] = $tags['#redCircle#']." Erreur de saisie du numéro de plugin"."\n"."(numéro invalide ou ne pas saisir le nom du plugin)";
$tags['#descriptionAnnulerActions#'] = $tags['#redCircle#']." Actions sur les plugins annulées";
$tags['#descriptionActionInconnue)#'] = $tags['#redCircle#']." L'action \"variable(askActionOnPlugin)\" demandée sur les plugins n'est pas gérée";

$scenario->setTags($tags);

$tags = $scenario->getTags();

// Demande de backup d'un plugin
// Récupération de la liste des plugins installés
$dirs = array_values(array_filter(scandir($tags['#pluginsPath#']), "pluginsFilter"));
$scenario->setLog('Nombre de plugins installés : ' . sizeof($dirs));

$tags['#installedPluginsList#'] = '';
$tags['#installedPluginsSize#'] = sizeof($dirs);
if ($tags['#installedPluginsSize#'] != 0) {
  $pluginList = array_map('pluginRenderer', $dirs, array_keys($dirs));
  // Découpage des plugins par paquets de NB_PLUGINS_GROUPED
  $chunkPluginsList = array_chunk($pluginList, $tags['#nbPluginsGrouped#']);
  $scenario->setLog('Nombre de messages : ' . sizeof($chunkPluginsList));
  // Envoi de la liste des plugins par paquets
  array_map('sendMessage', $chunkPluginsList);
}

$namescenario .= 'Exécuté par le scénario "' .$scenario->getName().'", le ' .date('d/m/Y');
$tags['#nameScenario#'] = $namescenario;

$scenario->setTags($tags);

$tags = $scenario->getTags();

// Demande de sauvegarde d'un plugin
// Récupération de la liste des plugins installés
$scenario->setLog('Récupération de la liste des plugins installés dans le répertoire ' . $tags['#pluginsPath']);
$dirs = array_values(array_filter(scandir($tags['#pluginsPath#']), "pluginsFilter"));
$pluginId = $scenario->getData("askPluginChoice");
$tags['#pluginToBackup#'] = (is_numeric($pluginId) && $pluginId <= sizeof($dirs)) ? $dirs[$pluginId - 1] : "";

$scenario->setLog("Lancement du backup du plugin " . $tags['#pluginToBackup#']);

$scenario->setTags($tags);

$tags = $scenario->getTags();

// Demande de restauration d'un plugin
// Récupération de la liste des plugins sauvegardés
$dirs = array_values(array_filter(scandir($tags['#backupPath#']), "pluginsFilter"));
$scenario->setLog('Nombre de plugins sauvegardés : ' . sizeof($dirs));

$tags['#savedPluginsList#'] = '';
$tags['#savedPluginsSize#'] = sizeof($dirs);
if ($tags['#savedPluginsSize#'] != 0) {
  $pluginList = array_map('pluginRenderer', $dirs, array_keys($dirs));
  // Découpage des plugins par paquets
  $chunkPluginsList = array_chunk($pluginList, $tags['#nbPluginsGrouped#']);
  $scenario->setLog('Nombre de messages : ' . sizeof($chunkPluginsList));
  // Envoi de la liste des plugins par paquets
  array_map('sendMessage', $chunkPluginsList);
}

$scenario->setTags($tags);

$tags = $scenario->getTags();

// Demande de récupération d'un plugin
// Récupération de la liste des plugins sauvegardés
$scenario->setLog('Récupération de la liste des plugins sauvegardés dans le répertoire ' . $tags['#backupPath#']);
$dirs = array_values(array_filter(scandir($tags['#backupPath#']), "pluginsFilter"));
$pluginId = $scenario->getData("askPluginToRestore");
$tags['#pluginToRestore#'] = (is_numeric($pluginId) && $pluginId <= sizeof($dirs)) ? $dirs[$pluginId - 1] : "";

$scenario->setLog("Lancement de la restauration du plugin " . $tags['#pluginToRestore#']);

$scenario->setTags($tags);

$tags = $scenario->getTags();

// Récupération de la liste des plugins sauvegardés
$scenario->setLog('Récupération de la liste des plugins sauvegardés dans le répertoire ' . $tags['#backupPath#']);
$dirs = array_values(array_filter(scandir($tags['#backupPath#']), "pluginsFilter"));
$scenario->setLog('liste répertoires sauvegardés : ' . $dirs);

$tags['#pluginsToRemoveList#'] = '';
$tags['#pluginsToRemoveSize#'] = sizeof($dirs);
if ($tags['#pluginsToRemoveSize#'] != 0) {
  $pluginList = array_map('pluginRenderer', $dirs, array_keys($dirs));
  // Découpage des plugins par paquets
  $chunkPluginsList = array_chunk($pluginList, $tags['#nbPluginsGrouped#']);
  $scenario->setLog('Nombre de messages : ' . sizeof($chunkPluginsList));
  // Envoi de la liste des plugins par paquets
  array_map('sendMessage', $chunkPluginsList);
}

$scenario->setTags($tags);

$tags = $scenario->getTags();

// Demande de suppression d'une sauvegarde de plugin
// Récupération de la liste des plugins sauvegardés
$scenario->setLog('Récupération de la liste des plugins sauvegardés dans le répertoire ' . $tags['#backupPath']);
$dirs = array_values(array_filter(scandir($tags['#backupPath#']), "pluginsFilter"));
$pluginId = $scenario->getData("askPluginToRemove");
$tags['#pluginToRemove#'] = (is_numeric($pluginId) && $pluginId <= sizeof($dirs)) ? $dirs[$pluginId - 1] : "";

$scenario->setLog("Lancement de la suppression de la sauvegarde du plugin " . $tags['#pluginToRemove#']);

$scenario->setTags($tags);
