/* @noodom / Scénario de gestion des plugins Jeedom depuis Discord (Sauvegarde, Restauration, Mise à jour, Suppression de sauvegardes)
Nom du scénario : Exécution d actions sur plugins

Installation du scénario : https://github.com/noodom/jeedom_scenarios/blob/master/updatePlugins/README.md
(Appelé par le scénario "Demande d actions sur plugins Telegram")

/!\ ATTENTION /!\ Ce scénario modifie le contenu du répertoires contenant les plugins Jeedom. 
/!\ Je vous laisse seul juge de la pertinence et de votre confiance à utiliser ce scénario pour la gestion de l'installation de vos plugins.
/!\ Je décline donc toute responsabilité pour tout problème créé suite à l'exécution de ce scénario./!\ 

*/

$tags = $scenario->getTags();

$tags['#redCircle#'] = "&"."#x1F534;";
$tags['#orangeCircle#'] = "&"."#x1F7E0;";
$tags['#greenCircle#'] = "&"."#x1F7E2;";
$tags['#greenCheck#'] = "&"."#x2705;";
$tags['#whiteCheck#'] = "&"."#x2611;";
$tags['#arrow#'] = "&"."#x27A1;";

// Récupération de l'action demandée (passée en paramètre du scénario)
(!isset($tags['#action#'])) ? $tags['#action#'] = "" : null;
// Vérification des actions autorisées
($tags['#action#'] != "backup" && $tags['#action#'] != "restore" && $tags['#action#'] != "remove")?$tags['#action#'] = "":null;

// Récupération du plugin demandé (passé en paramètre du scénario)
(!isset($tags['#plugin#'])) ? $tags['#plugin#'] = "" : null;

// Signature du scénario pour messages Discord
$tags['#scenarioName#'] = 'Exécuté par : ' . $scenario->getName();

// définitions titres, descriptions
$tags['#titreActionErreur#'] = $tags['#whiteCheck#']."Erreur sur intervention sur les plugins".$tags['#whiteCheck#'];
$tags['#titreBackupJeedom#'] = $tags['#whiteCheck#']."Sauvegarde de Jeedom".$tags['#whiteCheck#'];

$tags['#descriptionActionErreur#'] = $tags['#redCircle#']." Erreur : action non pécisée ou non valide (actions possibles : <b>backup</b>, <b>restore</b> ou <b>remove</b>)";
$tags['#descriptionActionInconnue#'] = $tags['#redCircle#']." Il y a eu un problème sur une action sur un plugin";
$tags['#descriptionDebutBackupJeedom#'] = $tags['#orangeCircle#']." La sauvegarde de Jeedom vient de se lancer";
$tags['#descriptionFinBackupJeedom#'] = $tags['#greenCircle#']." La sauvegarde de Jeedom vient de se terminer";

$namescenario .= 'Exécuté par le scénario "' .$scenario->getName().'", le ' .date('d/m/Y');
$tags['#nameScenario#'] = $namescenario;

if ($scenario->getRealTrigger() == "begin_backup" || $scenario->getRealTrigger() == "end_backup") {
  $tags['#action#'] = $scenario->getRealTrigger();
  $tags['#plugin#'] = "Jeedom";
}

$scenario->setTags($tags);

$tags = $scenario->getTags();

// création du dossier de sauvegarde des plugins si inexistant
if (!is_dir($tags['#backupPath#'] . '/.')) {
  // on crée le répertoire du plugin
  $scenario->setLog("création du répertoire de sauvegarde '" . $pluginDir ."'");
  mkdir($tags['#backupPath#']);
}
// création du dossier de destination si inexistant
$pluginDir = $tags['#backupPath#'] . '/' . $tags['#plugin#'];
if (!is_dir($pluginDir . '/.')) {
  // on crée le répertoire du plugin
  $scenario->setLog("création du répertoire '" . $pluginDir ."'");
  mkdir($pluginDir);
}
else {
  // répertoire existant, on vide les sauvegardes existantes
  $scenario->setLog("suppression de la sauvegarde précédente '" . $pluginDir . '/*.tar.gz\'');
  array_map('unlink', glob($pluginDir.'*.tar.gz'));
}

// backup du plugin
$scenario->setLog('Backup => cd ' . $tags['#pluginsPath#'] . '/' . $tags['#plugin#'] . '; tar cfz "' . $pluginDir . '/' . $tags['#plugin#'] . '.tar.gz" . > /dev/null');
system('cd ' . $tags['#pluginsPath#'] . '/' . $tags['#plugin#'] . '; tar cfz "' . $pluginDir . '/' . $tags['#plugin#'] . '.tar.gz" . > /dev/null');

//$scenario->setTags($tags);

$tags = $scenario->getTags();

// création du dossier de destination si inexistant
$pluginDir = $tags['#pluginsPath#'] . '/' . $tags['#plugin#'];
if (is_dir($pluginDir . '/.')) {
  // répertoire existant, on le supprime
  rrmdir($pluginDir);
}

// on crée le répertoire du plugin
$scenario->setLog("création du répertoire " . $pluginDir);
mkdir($pluginDir);

$pluginDir = $tags['#pluginsPath#'] . '/' . $tags['#plugin#'];
$scenario->setLog('Restore => cd ' . $pluginDir . '; tar xfz "' . $tags['#backupPath#'] . '/' . $tags['#plugin#'] . '/' . $tags['#plugin#'] . '.tar.gz"');
system('cd ' . $pluginDir . '; tar xfz "' . $tags['#backupPath#'] . '/' . $tags['#plugin#'] . '/' . $tags['#plugin#'] . '.tar.gz"');

//$scenario->setTags($tags);

$tags = $scenario->getTags();

// suppression du dossier du plugin à supprimer
$pluginDir = $tags['#backupPath#'] . '/' . $tags['#plugin#'];
if (!is_dir($pluginDir . '/.')) {
  // pas de répertoire du plugin, on ne fait rien
  $tags['#description#'] = $tags['#redCircle#'].' Pas de répertoire ' . $pluginDir . ' à supprimer, aucune action effectuée';
}
else {
  // répertoire existant, on le supprime
  rrmdir($pluginDir);
  $tags['#description#'] = $tags['#greenCircle#'].' La suppression du plugin <b>' . $tags['#plugin#'] . '</b> s\'est bien passée';
}

$scenario->setTags($tags);
